DROP TABLE IF EXISTS `blog_photo`; 
DROP TABLE IF EXISTS `blog_comment`; 
DROP TABLE IF EXISTS `blog_category_article`; 
DROP TABLE IF EXISTS `blog_article`; 
DROP TABLE IF EXISTS `blog_category`; 

#
# TABLE STRUCTURE FOR: blog_category
#

CREATE TABLE `blog_category` (
  `category_id` int(20) unsigned NOT NULL AUTO_INCREMENT,
  `category_name` varchar(100) NOT NULL,
  `description` text,
  PRIMARY KEY (`category_id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

INSERT INTO blog_category (`category_id`, `category_name`, `description`) VALUES (1, '(My Novel) The Story of Pale Blue Dot', 'The Story of Pale Blue Dot');
INSERT INTO blog_category (`category_id`, `category_name`, `description`) VALUES (2, 'science', NULL);
INSERT INTO blog_category (`category_id`, `category_name`, `description`) VALUES (3, 'hiburan', NULL);


#
# TABLE STRUCTURE FOR: blog_article
#

CREATE TABLE `blog_article` (
  `article_id` int(20) unsigned NOT NULL AUTO_INCREMENT,
  `article_title` varchar(100) NOT NULL,
  `article_url` varchar(100) NOT NULL,
  `date` datetime NOT NULL,
  `author_user_id` int(20) unsigned NOT NULL,
  `content` text,
  `allow_comment` tinyint(3) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`article_id`),
  UNIQUE KEY `article_title` (`article_title`),
  UNIQUE KEY `article_url` (`article_url`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

INSERT INTO blog_article (`article_id`, `article_title`, `article_url`, `date`, `author_user_id`, `content`, `allow_comment`) VALUES (1, 'Prolog', 'Prolog_1', '2012-12-13 01:02:46', 1, '<p>\n	&nbsp;</p>\n<p lang=\"zxx\" style=\"margin-bottom: 0in\">\n	<font size=\"2\" style=\"font-size: 11pt\">Bumi dua ribu tahun setelah sekarang, bukanlah tempat yang nyaman untuk didiami. Kota-kota tua dan puluhan pulau kecil mengapung di atas permukaan laut. Sebagian komunitas yang lain, memilih untuk hidup di bawah air, berlindung dari kejamnya radiasi ultra-violet. Tidak ada benua. Hampir semua daratan yang pernah kita kenal kini telah tenggelam dalam berjuta-juta ton air laut. Tidak ada lagi yang disebut negara, tidak ada satupun supremasi politik yang benar-benar diakui bersama. Hanya ada sejumlah federasi lokal mirip senat untuk mengatur hal-hal yang sepertinya resmi. Namun federasi ini tak pernah cukup kuat untuk mengikat para pemikir bebas yang sudah sedemikian liar. Manusia telah begitu sibuk untuk memikirkan bagaimana cara bertahan hidup. Terlalu sibuk untuk mengurusi masalah politik dan agama.</font></p>\n<p lang=\"zxx\" style=\"margin-bottom: 0in\">\n	&nbsp;</p>\n<div style=\"page-break-after: always;\">\n	<span style=\"display: none;\">&nbsp;</span></div>\n<p lang=\"zxx\" style=\"margin-bottom: 0in\">\n	<font size=\"2\" style=\"font-size: 11pt\">Bahkan sebenarnya, sudah tidak ada lagi manusia. Tidak, jika kau memandang manusia sebagai untaian hidrokarbon yang memiliki akal sehat. Di masa itu tak akan ada yang sanggup menemukan seseorang tanpa embeded system di dalam otaknya. Kalaupun ada, pasti tidak akan lebih dari setengah persen penduduk dunia. Orang-orang yang masih mempertahankan keaslian organiknya hanyalah orang-orang pinggiran tanpa masa depan. Mereka tidak bisa membeli apapun di pasar elektronik. Mereka bahkan tidak bisa berkomunikasi dengan siapapun. Mulut, telinga, dan otak mereka terlalu lambat untuk menangkap dentuman-dentuman bit biner itu. Mereka hidup terisolasi, seperti spesimen binatang langka yang tidak diharapkan. Sebagian besar dari mereka terlalu miskin untuk memodifikasi tubuhnya. Mereka seolah sudah ditakdirkan untuk hidup tidak lebih lama dari dua puluh tahun. Sebagian yang lain terlalu fanatik untuk tidak mengubah sedikitpun rancangan biologis yang diberikan oleh Tuhan. Sebuah pernyataan iman yang tidak akan pernah didengar siapapun.</font></p>\n<p lang=\"zxx\" style=\"margin-bottom: 0in\">\n	&nbsp;</p>\n<p lang=\"zxx\" style=\"margin-bottom: 0in\">\n	<font size=\"2\" style=\"font-size: 11pt\">Perang dunia terakhir, bahkan tidak ada yang ingat itu perang dunia keberapa, telah menghapuskan banyak kata dan kenangan. Dua ratus tahun telah berlalu semenjak peristiwa paling mengerikan dalam sejarah bumi itu. Spesies baru yang bahkan tidak memiliki nama, kini perlahan menggeliat dari keterpurukannya. Berusaha menggapai kejayaan masa lampau. Sebuah chip mungil di belakang kepala mereka berfungsi sebagai buffer, untuk menangkap sinyal-sinyal digital dan menyimpannya sementara, sebelum bisa diproses oleh sel-sel syaraf mereka yang terlalu lambat. Kalau bisa diberi nama, biarlah spesies penerus homo sapiens ini diberi nama cyborg.</font></p>\n', 0);
INSERT INTO blog_article (`article_id`, `article_title`, `article_url`, `date`, `author_user_id`, `content`, `allow_comment`) VALUES (2, 'Kelahiran Penyelamat Dunia', 'Kelahiran-Penyelamat-Dunia', '2012-12-13 01:04:42', 1, '<p>\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Tidak ada seorangpun yang tahu arti nama &#39;Set&#39;. Bagi mereka, nama itu mungkin berarti himpunan. Sebuah istilah yang sangat sering diperdengarkan dalam pelajaran matematika dasar. Tidak ada seorangpun yang pernah mendengar ataupun membaca bahwa nama itu adalah nama salah satu dewa mesir kuno. Nama yang berhubungan dengan sang dewa kegelapan.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Tidak seorangpun pula yang tahu apa arti nama &#39;Asgard&#39;. Itu bukan nama yang umum diperdengarkan. Sebuah dunia di mana Odin, Tor, dan para dewa viking mendiaminya.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Namun, hampir semua orang yang hidup di masa itu mengenal siapa penyandang nama &#39;Set Asgard&#39;. Bukan mengenal dalam arti sebenarnya. Hanya sekedar mengenal, mirip dengan bagaimana orang awam di masa kita mengenal kata &#39;quantum&#39;. Tidak banyak yang tahu artinya, tapi hampir semua orang memakainya untuk menggambarkan sesuatu yang kelihatannya ilmiah dan dahsyat.</font></p>\n<div style=\"page-break-after: always;\">\n	<span style=\"display: none;\">&nbsp;</span></div>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">&#39;Set Asgard&#39; adalah nama yang janggal. Bagaimanapun seorang dewa mesir tidak mungkin berasal dari asgard. Nama itu kedengarannya seperti nama seseorang yang sudah tersesat sejak lahir. Namun tidak ada seorangpun yang peduli. Tidak, bahkan sang pemberi nama itu sendiri. Ayah Set bahkan tidak tahu, nama macam apa yang telah ia berikan pada putra kecilnya yang baru lahir. Ia adalah seorang insinyur komputer. Pekerjaan itu kira-kira sama pentingnya dengan pekerjaan dokter bedah di masa kita. Tanpa campur tangannya dan jutaan insinyur komputer lain, tak akan ada seorangpun yang stabil. Otak biologis adalah salah satu hardware terumit yang pernah ada. Setiap kepala manusia di bumi seolah diisi oleh segumpalan kerutan jaringan neuron tidak jelas yang berbeda satu sama lain. Mereka memiliki konfigurasi dan interface yang hampir sama, namun tidak persis sama. Tidak mungkin bagi siapapun untuk memetakan semua jenis otak di dunia. Para ahli neurologi hanya dapat membuat beberapa golongan besar otak manusia. Dan merupakan pekerjaan seorang insinyur komputer untuk membuat antarmuka chip yang sesuai dengan pasien mereka. Proses pemasangan chip tersebut bukanlah proses sekali seumur hidup yang akan dialami semua orang dibumi. Proses itu lebih seperti proses penyesuaian seumur hidup yang tidak akan pernah selesai, sebelum dihentikan paksa oleh kematian. Keadaan itu masih diperparah dengan kondisi otak yang terus menerus berubah. Rasa sedih, trauma, kebahagiaan, pengalaman, atau apapun akan mengubah otak manusia untuk selamanya. Dalam hitungan bulan atau tahun, chip-chip yang tadinya berfungsi cukup baik akan berubah menjadi sangat menyakitkan. Seperti tumor ganas yang ditolak oleh tubuh.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Orang-orang seperti ayah Set selalu memiliki uang yang cukup untuk melakukan hampir segala sesuatu. Namun sayangnya, ia tak pernah punya waktu untuk melakukan satu hal pun tanpa gangguan dari siapapun. Maka kelak, di masa depan, Set yang walaupun memiliki ketertarikan pada bidang yang ditekuni ayahnya itu, memilih untuk tidak menjalani hidup seperti ayahnya.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Ibu Set meninggal akibat ketidak sesuaian chip di dalam kepalanya. Cukup ironis memang. Suaminya bahkan tak punya cukup waktu untuk memperbaiki chip dalam kepala istrinya. Wanita malang itu bahkan meninggal beberapa bulan sebelum melahirkan anaknya. Embrio Set yang perlahan mulai menampakkan bentuk pun terpaksa dipindah ke sebuah tabung pendukung kehidupan, sampai ia siap menatap dunia dari kedua matanya yang belum berkelopak. Pada akhirnya, Set akan tetap hidup. Memandang sang ayah dengan penuh kebencian. Memandang dunia dengan amarah yang tak terlukiskan.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Di usianya yang keenam tahun, Set pertama kali bersentuhan dengan interconn, sebuah istilah yang di masa kita kurang lebih sama artinya dengan internet. Bedanya, di masa itu, tidak lagi diperlukan komputer atau gadget apapun. Semua yang dibutuhkan sudah tertanam di ujung otak. Sedikit memejamkan mata dan berkonsentrasi akan membawa siapapun melebur dalam kesadaran kolektif yang disebut interconn. Bagi seorang anak berumur enam tahun, terhubung ke interconn sama artinya dengan berjalan menuju kegelapan. Interconn bukan tempat bagi anak-anak. Dan orang tua normal manapun tak akan memasang modul interconn sebelum anaknya siap. Ayah Set, walaupun telah banyak dibuat gila oleh pekerjaan dan kematian istrinya, tidaklah segila itu untuk memasang modul berbahaya di dalam kepala anaknya sendiri. Set muda yang cerdas kerap kali mengalami gangguan ketidak stabilan pada otaknya. Di masa itu siapapun akan lebih suka untuk menjadi tidak terlalu cerdas dan menjalani hidup yang stabil. Ketidak-stabilan pada hidup dan otak akan membawa rasa sakit yang demikian luar biasa. Sesuatu yang tidak ada padanan katanya di jaman kita.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Set yang terlalu cerdas itu, kadang mencoba mengkalibrasi ketidak-sesuaian chip di kepalanya sendiri. Ia tidak sudi menunggu ayahnya yang selalu terlalu sibuk untuk melakukan sesuatu. Bocah itu tampaknya berhasil. Ia berhasil menciptakan sebuah program yang ia simpan dalam memori eksternalnya. Barisan kode dalam bahasa pemrograman yang bahkan lebih tidak bisa dibaca dari bahasa assembly di masa kita. Setiap kali ia merasa ada yang tak beres, maka ia akan mengaktifkan modul-modul dengan parameter spesifik untuk membuat chip asing di dalam kepalanya itu bisa diterima oleh syaraf-syaraf biologis di sekitarnya. Set muda sama sekali tidak tahu bahaya yang diciptakannya sendiri. Ia hanya tahu bahwa sel syaraf asli berbeda dari sirkuit-sirkuit elektronik super-konduktor, namun ia tidak benar-benar paham seberapa berbedanya. Ia juga tidak tahu bahaya yang mengintip di balik interconn. Sebuah modul tambahan yang telah dirakitnya selama lima minggu. Kelak di kala Set telah menginjak usia remaja, sang ayah yang sudah semakin renta itu terpaksa memperbaiki ribuan kesalahan program dalam kepala sang anak. Penuh rasa jengkel sekaligus takjub, melihat kesalahan-kesalahan maupun manuver program yang bahkan ia sendiri tak pernah pikirkan.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Interconn adalah sebuah dunia lain yang tercipta melalui arus informasi. Walau disebut informasi, namun tidak semua yang ada di dalamnya mengandung kebenaran. Siapapun menuliskan apapun, berinteraksi dengan siapapun, tanpa identitas, maupun dengan identitas baru yang kedengaran keren. Setidaknya itulah yang ada dalam pikiran banyak orang awam di masa itu. Interconn adalah sebuah dunia baru dengan ratusan protocol teknis, namun tanpa aturan moral. Satu-satunya hukum dunia nyata yang berlaku di sana adalah hukum rimba. Siapa yang kuat, akan sanggup melakukan apapun. Termasuk membunuh orang lain dari kejauhan, dengan memformat habis pikirannya. Tanpa pernah tahu keadaan sebenarnya di interconn, Set melayang di tengah kerumunan jiwa. Mencari sekumpulan informasi yang tidak pernah didapatkannya dari sang ayah, ataupun koleksi memori eksternal di dalam kamarnya. Secara alami, Set memiliki sebuah mekanisme otomatis untuk melindungi dirinya. Ia tidak benar-benar tahu untuk apa, namun ia pikir sangat penting untuk memisahkan ruang publik dalam kepalannya dengan ruang privat. Sesuatu yang bahkan tidak dianggap terlalu penting oleh orang dewasa. Kebanyakan orang hanya memasang protokol perlindungan resmi seadanya, tanpa pernah tahu bahwa seorang hacker akan dapat menerobos masuk ke dalam pikiran dan hati mereka, mengambil kehendak bebas dari dalam jiwa mereka, dan menanamkan sebaris mantra digital untuk mengendalikan mereka dari tempat yang paling tersembunyi. Kenyataannya hampir ratusan orang terinfeksi malware saat terhubung ke interconn. Sesuatu yang membuat federasi lokal semakin kewalahan. Lagi-lagi, ahli-ahli syaraf, tukang forensik, dan insinyur-insinyur komputer seperti ayah Set harus berjuang keras menyelamatkan sejumput manusia dari kuasa bayangan yang entah berada di mana. Perusahaan-perusahaan security mulai gencar mengkampanyekan pentingnya keamanan, namun suara mereka seolah lenyap di antara hiruk pikuk dunia. Siapa yang akan peduli dengan keamanan, jika tidak tahu apa yang harus dimakan besok, dan bagaimana supaya tetap ada arus listrik yang mengalir dalam chip-chip di kepala mereka.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Beberapa tahun dilalui Set dengan menjelajahi interconn secara diam-diam. Ia seperti seorang murid penyihir yang membuka buku mantra kuno sebelum saatnya. Akan ada konsekuensi atas kecurangan Set. Sebuah bayaran mahal untuk permainan terlarangnya.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Hari itu, Set seperti biasanya menumpang pada sebuah kendaraan tanpa roda yang dimotori oleh aliran listrik pada jalanan super-konduktor di bawahnya. Sebuah kendaraan elit yang dimiliki oleh federasi lokal. Bersama dengan anak-anak seusianya, kendaraan itu mengarah pada sebuah distrik bangunan akademi, tempat di mana mereka akan menghabiskan waktu selama setengah hari untuk belajar berbagai hal yang dianggap penting. Tidak semua orang mendapatkan kehormatan duduk di bangku Akademi Shinra. Hanya beberapa orang anak pejabat pemerintahan lokal federasi, sejumlah anak orang kaya, dan Set, serta temannya Eirene Stark. Eirene adalah anak seorang ahli medan plasma terkenal. Jauh lebih terkenal dari ayah Set yang selalu sibuk. Sementara ibu Eirene adalah seorang wanita cantik yang kerapkali ikut membantu suaminya dalam meneliti radiasi matahari. Kedua anak itu sangat beruntung karena reputasi kedua ayah mereka. Ayah Eirene adalah salah satu pendiri Akademi Shinra. Lima tahun yang lalu, ia dan ribuan ilmuwan satu tim berhasil menciptakan medan plasma interferensi untuk melindungi penduduk dari radiasi matahari. Teknologi itu bukan suatu konsep baru, namun merupakan suatu solusi murah. Cukup murah sehingga cukup masuk akal untuk diwujudkan, ditengah ekonomi yang baru menggeliat akibat perang. Bagi sesuatu sebesar ekonomi dunia, butuh puluhan tahun untuk menggeliat sebelum akhirnya bisa sedikit mapan untuk menaungi orang-orang di dalamnya.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">&quot;Interconn, kau bilang?&quot;, Eirene menutup mulutnya sendiri, takut kalau teman-teman yang lain ikut mendengar. Anak-anak seusia mereka biasanya sudah cukup senang dengan ensiklopedi dalam memori eksternal, game-game virtual reality multiplayer yang terhubung oleh jaringan lokal kecil. Maka bagi mereka, interconn adalah sebuah dunia baru yang misterius, tempat di mana orang dewasa sendiri tidak banyak membicarakannya. &quot;Aku akan membawamu ke sana...&quot;, bisik Set dengan penuh percaya diri. Antara tertarik dan takut, Eirene menatap Set. Mirip seperti Adam yang hendak menerima buah terlarang dari sang Hawa. Kendaraan pun berhenti dengan perlahan. Cukup perlahan, hingga kelembaman inersia Newton pun hanya bisa mendorong mereka beberapa milimeter ke depan.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Eirene dan Set adalah sahabat sejak di akademi. Mereka tidak punya terlalu banyak ensiklopedi eksternal yang bisa dipertukarkan dengan teman-teman yang lain. Mereka juga tidak punya account untuk bergabung dalam permainan multiplayer apapun. Mereka hanya anak kecil. Orangtua Eirene selalu ingin agar anaknya memiliki kehidupan sosial di dunia nyata, bersama dengan anak-anak seusianya. Namun mereka salah, karena di masa itu sudah tidak banyak kehidupan di dunia nyata. Semua orang akan diam membisu, menikmati isi kepala mereka sendiri yang bercampur dengan memory eksternal. Beberapa orang dewasa akan berhubungan dengan rekan kerjanya melalui interconn. Beberapa yang lain hanya akan bersenang-senang di dunia maya, melupakan sejenak bahwa mereka punya kehidupan nyata. Dan karena itulah, Set adalah satu-satunya teman Eirene. Teman dalam arti sebenarnya, yang tidak hanya sekedar berbasa-basi sebentar, bertukar memori eksternal dan pergi tanpa peduli kapan akan bisa bertemu lagi. Kenyataannya mereka cukup beruntung dibandingkan dengan teman-teman sebayanya. Dalam lubuk hati teman-teman mereka, ada sebuah perasaaan tanpa nama yang kerap membuat mereka menangis tanpa ekspresi dan air mata. Sebuah perasaan yang di masa kita dinamakan kesepian. Anak-anak kecil itu seolah ditakdirkan untuk kehilangan sesuatu yang sebenarnya tidak pernah mereka miliki.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Pada saat liburan panjang, setelah sebuah sesi sarapan yang canggung antara ayah dan anak, Set berpamitan, hendak memenuhi undangan Eirene untuk bermain di rumahnya. Ayah Set hanya mengangguk saja, seolah lupa bagaimana mengucapkan kata &quot;iya&quot; dengan mulut biologisnya. Sang ayah mulai berpikir, betapa lebih cepatnya anak-anak di masa itu untuk menjadi dewasa. Ia masih ingat, bagaimana di tengah perang saudara yang berkecamuk, sebelum embeded system di dalam kepala manusia menjadi sepopuler saat ini, ia pertama kali mengajak ibu Set berjalan-jalan. Perlahan-lahan air mata menetes pelan pada kedua pipinya. Ada rasa bersalah, saat ia terlambat mengetahui apa yang terjadi pada istrinya. Sebuah kesalahan paling fatal dalam hidupnya.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Keluarga Eirene lebih tampak normal dalam pandangan kita. Kedua orangtuanya tidak sesibuk ayah Set. Tidak setiap hari ada medan plasma yang rusak. Kesibukan terbesar ayah Eirene justru adalah mengajar di sebuah universitas terkenal di distrik itu. Bahkan di masa itu, masih ada hal-hal yang hanya bisa dipelajari secara manual. Memang proses-proses seperti menghapalkan sesuatu sudah tidak sesulit di masa kita. Bahkan bisa dikatakan proses itu tidak ada. Yang ada hanyalah bagaimana seseorang bisa meng-indeks triliunan informasi dalam kepala dan koleksi memori eksternal. Di masa itu ada semacam bisnis baru. Kursus mengindeks data, yang kurang lebih sama omong kosongnya dengan pengaktifan otak tengah di masa kita.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Kenyataannya, justru keluarga Eirene bukanlah keluarga yang normal di masanya. Mereka terlalu banyak berkomunikasi dengan cara tradisionil yang lambat. Bagi masyarakat di masa itu, berbicara berlama-lama untuk benar-benar bertukar informasi sama kunonya dengan berkiriman surat kertas di jaman kita. Ibu Eirene menyambut Set dengan ramah, memanggil anak perempuannya untuk turun. Sang ayah agak acuh, tapi masih tetap ramah. Set membawa sekotak perangkat mekanik yang aneh. Awalnya ia menunjukkan hasil karyanya, sebuah robot hexapod yang bisa bergerak dan berbicara. Menjawab pertanyaan ala kadarnya. Saat itu, ayah Eirene sempat terbelalak. Tidak ada seorang anak berusia delapan tahun yang bisa membuat kecerdasan buatan seperti itu. Sangat tidak masuk akal, bahkan mengingat fakta bahwa Set adalah anak seorang insinyur komputer pendiam yang handal. Tentu saja Set tidak belajar semua itu dari sang ayah. Ada suatu ruang besar di interconn. Semacam ensiklopedi gratis di mana semua orang bisa berbagi apapun, sejauh mereka mau membaginya. Ruang itulah yang selalu dikunjungi Set, selain beberapa hal lain yang menarik baginya.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Eirene memandang robot buatan Set dengan takjub. Lalu seperti anak-anak yang lain, mereka mulai bermain multiplayer game yang sudah usang. Sebuah permainan yang begitu kuno, sehingga tidak bisa dibicarakan dengan teman-teman mereka yang lain. Saat itu orangtua Eirene merasakan ada getaran kecil di ujung sel syaraf mereka. Ada suara seseorang yang berbicara mengenai kebocoran medan plasma. Kedengarannya cukup gawat, sehingga mereka langsung pergi.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">&quot;Orang dewasa selalu seperti itu, sebenarnya ada apa?&quot;, Eirene bergumam. &quot;Itu interconn,.. mereka pasti dapat suatu kabar dari seseorang, mungkin pekerjaan...&quot;, jawab Set serius. Seperti yang bisa ditebak, percakapan mereka mengarah pada satu hal. Interconn, sebuah dunia misterius tempat di mana rasa takjub dan takut menyeruak dari dalam diri siapapun. &quot;Berikan kode pin chip mu, aku akan memodifikasinya dari sini, sehingga kita bisa ke interconn bersama-sama&quot;. Tatapan Set tampak sangat meyakinkan. Ia tahu pasti, bahwa dengan mengetahui kode fisik chip seseorang, iya bisa memodifikasi chip tersebut sampai taraf tertentu. Sesuatu yang pernah ia lakukan pada dirinya sendiri dua tahun yang lalu. Namun ada satu hal yang tidak disadari Set. Satu hal yang bahkan membuat ayahnya harus berpikir keras beberapa hari sebelum menyanggupi akan memasang chip dalam kepala pelanggannya. Konfigurasi otak manusia tidak pernah persis sama, dan ia tidak pernah tahu sebelumnya, bagaimana konfigurasi otak temannya. </font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Eirene berbaring tak bergerak, nyaris tanpa nafas. Perlu waktu agak lama bagi Set untuk menyadari bahwa ia bukan Tuhan yang bisa melakukan segala sesuatu, dan mengkoneksikan chip Eirene ke interconn adalah salah satu dari sekian banyak hal yang tidak mungkin ia lakukan. Sebuah kesalahan fatal bahkan nyaris membakar sebagian sistem syaraf tak sadar dalam kepala Eirene. Cukup parah sehingga kedua orang tua gadis itu kelak akan membenci Set seumur hidup mereka.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">&quot;Dari mana kau tahu itu?,..... dari interconn?..... dan bagaimana pula kau bisa sampai ke sana,...&quot;, ayah Set terbelalak kaget, sementara ibu Eirene menangis di sudut rumah sakit. Ayah Set yang pendiam itu mengguncang-guncang tubuh anaknya yang lemas karena terlalu banyak menangis. Sang ayah merasa sangat marah dan gagal. Pertama ia tidak sempat melakukan apapun untuk menyelamatkan istrinya, kedua ia tak melakukan apapun untuk mengajari anaknya itu tentang banyak hal. Ayah Eirene, dengan sikap bijaksana yang dipaksakan berusaha menenangkan pria malang itu. Set dapat melihat bahwa di sudut mata pria itu, ada semacam kebencian yang disembunyikan. Kebencian pada seorang anak laki-laki yang sudah membahayakan nyawa putrinya.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Setelah proses operasi berhari-hari, Eirene tampak kaku. Ia kesulitan mengatakan apapun. Ia bahkan tak sanggup mengingat apapun. Ia terlalu lemah untuk menangis. Di hadapannya tampak ayah dan ibunya yang berharap cemas agar sang putri dapat hidup normal kembali. Ayah Set berjuang keras dengan tangan gemetar, sedikit kesalahan lagi, dan sudah jelas, bahwa anaknya harus menerima hukuman panjang dari federasi lokal. Bukan hanya Set, bahkan nyawanya sendiripun ikut terancam. Sebagai seorang ayah yang gagal, yang menyebabkan anak semata wayangnya menjadi sedemikian berbahaya, hukuman dari federasi lokal pasti akan menimpanya.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Masyarakat memang tidak adil. Tidak pernah adil. Mereka memeras tenaga, pikiran, dan waktu dari orang-orang seperti ayah Set. Memeras kehidupan pria malang itu, merampas istri dan anaknya. Dan sekarang mereka mengecamnya sebagai ayah yang gagal. Kecaman yang hanya bisa ditanggapinya dengan senyum miring menjengkelkan. Sebuah reaksi keputus asaan yang justru memancing lebih banyak kecaman.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Berbulan-bulan setelah itu, Eirene kembali pulih. Ia tetap melanjutkan hidupnya, dengan kepingan waktu yang telah hilang selamanya. Ia menjadi sangat pendiam dan sangat ketakutan. Sementara Set dikeluarkan dari akademi. Modul interconn asal-asalan yang dirakitnya sendiri di nonaktifkan dengan paksa. Kedua sahabat itu kini berdiri sangat jauh satu sama lain. Ada perasaan bersalah yang sangat besar dalam diri Set, sehingga hukuman macam apapun seolah tidak bisa membuatnya merasa telah terhukum. Kini Set tak lagi bisa masuk ke interconn. Federasi lokal bahkan mempertimbangkan untuk memperpanjang hukuman itu menjadi seumur hidup. Anak malang itu akan hidup dalam kegelapan untuk selamanya. Perasaan itu terasa semakin menyakitkan. Bukan karena ia takut pada kegelapan, namun seolah ada mata ketiga yang dicabut paksa dari dalam kepalanya. Mata yang selama ini membuatnya bisa melihat keajaiban dan keindahan interconn. Dengan perasaan tak menentu Set menutup kedua matanya yang lain, memandang kegelapan pekat dalam dirinya, kegelapan yang pada akhirnya justru akan menyelamatkan dunia.</font></p>\n', 0);
INSERT INTO blog_article (`article_id`, `article_title`, `article_url`, `date`, `author_user_id`, `content`, `allow_comment`) VALUES (3, 'Bidak Bayangan', 'Bidak-Bayangan_1', '2012-12-13 01:06:27', 1, '<p>\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Sepuluh tahun telah berlalu semenjak tragedi itu. Set kini telah tumbuh menjadi seorang pemuda muram. Jauh lebih muram dan pendiam dari sang ayah yang sudah semakin renta. Ia tidak pernah tahu dan tidak pernah mau tahu tentang apa yang terjadi pada Eirene sekarang. Di usianya saat ini, sudah seharusnya ia berhak menyentuh interconn. Namun tragedi masa lalu itu telah mengubah semuanya. Set dianggap terlalu berbahaya. Bukan hanya untuk orang-orang di sekitarnya, tapi juga untuk dirinya sendiri.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Kejadian itu berlangsung sangat cepat, namun tidak sedemikian cepatnya berlalu dari ingatan sang ayah. Waktu itu Set mengerang dan bergulingan di lantai. Tepat sesudah ia menyelesaikan sebuah program kecil pengatur pernafasan. Rencananya program itu akan dipasangkan untuk mengontrol paru-paru buatan pada seorang anak kecil pucat yang menderita kelainan sejak lahir. Teriakan itu membuat ayah Set dan beberapa orang temannya membanting chip-chip komputer yang sudah hampir siap diprogram. Sebuah harga yang harus dibayar dengan sangat mahal, namun setidaknya tak semahal nyawa putranya. Tahulah ayah Set, bahwa putra jeniusnya itu tidak hanya lancang mengembed modul interconn. Ada jutaan sirkuit software tambal sulam di sana. Ia terlalu gemetar untuk melihat bagaimana para ahli neurologi membedah otak anaknya, mengambil chip yang sudah bertahun-tahun berada di sana tanpa kalibrasi. Sebenarnya bukan tanpa kalibrasi, namun justru terlalu sering dikalibrasi secara diam-diam. Tidak ada yang pernah tahu bagaimana Set melakukan itu semua. Tidak ada pula yang punya cukup waktu untuk memperhatikan catatan medis sang pemuda. Sebuah catatan yang nyaris bersih dari proses penyesuaian chip.</font></p>\n<div style=\"page-break-after: always;\">\n	<span style=\"display: none;\">&nbsp;</span></div>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Butuh waktu yang cukup lama untuk memasang kembali chip yang berbeda ke dalam kepala Set. Mereka takut kalau pemuda itu melakukan modifikasi lagi, melebihi apa yang seharusnya boleh dilakukan. Butuh waktu yang lebih lama pula untuk membuat program baru yang lebih mapan dan bebas kesalahan. Sebuah program yang didesain sangat baik oleh belasan insinyur komputer profesional. Namun waktu yang paling lama adalah waktu yang dibutuhkan oleh Set untuk beradaptasi dengan chip barunya. Ia selalu merasa seperti memakai helm kekecilan yang tidak bisa dilepas. Tidak ada lagi yang bisa dilakukannya. Nomor serial chip itu benar-benar disembunyikan. Set merasa program baru di kepalanya itu terlalu lambat. Memang kode-kodenya terlihat lebih elegan, sehingga lebih mudah diperbaiki atau diubah jika ada kesalahan, namun sesuatu yang mudah dibaca manusia, justru sangat sulit dibaca oleh chip silikon lapis emas dalam kepalanya. Kadang respon yang diberikan Set pada dunia luar terasa lambat, sangat lambat untuk ukuran kecerdasannya yang luar biasa.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Hari itu, hari di mana mereka mengambil chip lama dalam kepala Set, adalah hari penyelamatan bagi dirinya. Ada sebuah bahaya besar yang mengintip di sana. Jauh lebih besar dari meledaknya sebagian sel syaraf di otak Set.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Lima bulan sudah berlalu semenjak peristiwa mengagetkan yang menimpa putra sang insinyur komputer malang itu. Kali ini muncul peristiwa lain yang tak kalah mengagetkan. Seorang petugas keamanan dari pasukan federal lokal, tiba-tiba menembaki beberapa orang rekannya. Ia lalu membuat sebuah lambang aneh dari darah mereka, sebelum akhirnya menembak kepalanya sendiri.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Pria itu ternyata sudah lama mati. Jauh sebelum ia mengakhiri hidupnya. Para petugas forensik di tempat ayah Set bekerja hanya bisa menemukan, bahwa pikiran sang petugas keamanan telah digantikan oleh semacam pikiran baru yang sangat terstruktur. Sebuah program komputer yang demikian kejam, entah ditanamkan oleh siapa dan untuk apa. Mereka hanya bisa mengarah pada satu kata, interconn. Sebuah dunia yang sangat asing. Terlalu asing, sehingga setiap orang dapat melakukan apapun di sana.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Namun peristiwa mengejutkan itu hanyalah sebuah awal dari kejadian panjang yang akan terjadi sampai bertahun-tahun setelahnya. Federasi-federasi lokal saling memandang dengan penuh kecurigaan. Perusahaan security mencapai puncak kejayaannya. Suap dan permainan politik kotor mulai menampakkan taringnya, seiring dengan pergerakan ekonomi yang semakin kuat. Ada koalisi-koalisi tersembunyi. Setiap federasi lokal akan mulai mengutuki para hacker di depan publik, namun membayar mereka dengan penuh kecurigaan dan ketakutan. Kekacauan mulai sering terjadi. Set dan ayahnya semakin sibuk memenuhi pesanan beberapa orang yang ingin mencabut modul interconn dengan sangat ketakutan.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">&quot;Hei,... Set,... bagaimana itu? Serangan hacker?&quot;, Joe, salah seorang petugas kebersihan di tempat bekerja mereka mulai mengawali pembicaraannya dengan membuka sebotol bir. Pria paruh baya itu adalah satu-satunya teman Set setelah ia kehilangan Eirene. Teman yang benar-benar teman, bukan seperti sekumpulan rekan kerjanya yang membisu serius sambil tersenyum dibuat-buat. &quot;Kau tidak akan ingin tahu,...&quot;, sahut Set. Namun pria itu terus mendesak, sampai Set mau bicara. &quot;Lihat bir ini, pertama alkohol akan menyentuh jaringan pencernaanmu, menjadikannya lebih aktif, sehingga kau akan merasa lapar. Kemudian akan masuk ke dalam saluran darahmu, membentur saraf-saraf dalam kepalamu. Kau akan mulai bicara dengan sangat aktif, sampai susah didengar. Kemudian jika kau terus minum, maka kau tak akan lagi bisa mengendalikan diri dan mabuk. Terakhir waktu ia mengenai sistem syaraf tak sadarmu, gerakan jantung dan paru-parumu tidak akan menjadi normal. Mungkin berhenti tiba-tiba, dan kau akan mati,....&quot;. Penjelasan Set diiringi dengan semburan bir yang keluar dari mulut Joe. &quot;Hei,... tidak separah itu kan?&quot;, pria paruh baya itu tampak pucat dengan busa bir yang masih melekat di langit-langit mulutnya. &quot;Tidak,... tapi jauh lebih parah&quot;, ucap Set santai, seraya ikut menenggak bir. &quot;Mereka ada di suatu tempat yang kau sendiri tidak tahu di mana. Memasang suatu malware dalam ruang privat di otakmu. Entah melalui interconn atau pertukaran memori eksternal. Mereka memerintahkanmu untuk melakukan sesuatu, seolah-olah itu memang keinginanmu, lalu mereka membunuhmu kalau sudah bosan&quot;. Kali ini Set tampak lebih serius. </font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Dalam pikirannya terkenang betapa bodohnya, saat ia hampir saja merusak jaringan syaraf Eirene dengan ketidak tahuannya. Terselip pula sederet kebencian pada sebuah bayangan hitam yang tak dikenalnya. Mereka yang entah karena alasan apa mencabut nyawa orang lain, setelah mengendalikan mereka seperti bidak di papan catur. Ia masih bisa mendengar jeritan seorang ibu yang tiba-tiba kalap dan mencekik anaknya sendiri. Tepat di bawah jendela tempat ia bekerja. Sang ibu akhirnya meninggal setelah sejumlah usaha untuk membersihkan malware itu tak membuahkan hasil, sementara sang anak menangis ketakutan. Teringat akan wajah ibunya yang selembut malaikat tiba-tiba berubah menjadi iblis.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">&quot;Ya ampun Set,... ini sudah botol ketiga yang kau minum sendirian,... apa kau lupa apa yang baru saja kau ceritakan sendiri&quot;, Joe berusaha mencegah temannya yang masih muda itu untuk tidak mati karena terlalu banyak minum. Ia tahu betapa berat kehidupan yang dijalani Set. Selama dua puluh tahun bekerja di sana, Joe sudah sangat mengenal ayah Set. Ia ada di sana, saat insinyur komputer itu menanggapi kematian istrinya dengan dingin dan hati yang hancur. Ia juga di sana waktu pria itu untuk pertama kalinya menangis. Berdoa pada Tuhan atau siapapun untuk menyelamatkan Eirene dari kematian, dan Set dari hukuman mati. Joe juga ada saat Set berteriak memegangi kepalanya sambil berguling di lantai, untuk kemudian pingsan selama beberapa hari. Maka dibiarkannya Set yang tanpa harapan itu menenggak kepahitan hidupnya sendiri.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Federasi Solaris telah jatuh. Demikian isi berita hari ini. Sebuah berita yang bercokol di setiap sudut interconn dan setiap sudut mulut penduduk di kota itu. Tentu saja, semenjak serangan para hacker, berita seperti ini sudah bisa diduga sebelumnya. Namun berita itu menjadi sangat penting, karena Federasi Solaris hanya berjarak beberapa mil dari federasi mereka.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Di tengah keputus asaan, mereka tak lagi bisa memikirkan cara untuk bertahan. Ego dan nafsu adalah sesuatu yang harus dipenuhi keinginannya agar tidak mati dalam keadaan penasaran. Maka perampokan, pembunuhan, perkosaan dan kejahatan-kejahatan khas manusia pun memenuhi kota itu, bahkan memenuhi seluruh dunia. Menandakan bahwa seperti apapun wujud penduduk dunia sekarang, mereka cuma manusia-manusia lemah yang mudah ditebak.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Daerah tempat tinggal dan bekerja Set masih tergolong cukup aman. Surga yang tak terlalu indah jika dibandingkan dengan sekelilingnya. Semua orang bekerja delapan jam sehari, untuk kemudian beristirahat ala kadarnya, mengendapkan asam laktat kelelahan, dan mengisi kembali arus listrik dalam chip di kepala mereka, serta bagian-bagian cybernetic lain. Di tengah waktu istirahatnya yang berharga, mereka sekali-kali bangun, memastikan tidak ada panggilan darurat kebakaran. Tapi setidaknya di sana tidak ada serangan yang cukup berarti, selain lemparan bom molotov dari masyarakat pinggiran yang terabaikan.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">&quot;Eirene,.... aku ingin tahu bagaimana dia sekarang&quot;, Set bergumam antara sadar dan tidak, ditemani oleh Joe yang juga antara peduli dan tidak. Hampir setiap malam, sebelum pulang, mereka mampir ke sebuah bar kecil di seberang tempat mereka bekerja. Ayah Set pernah sesekali bergabung. Menenggak segelas ale, dan pulang begitu saja setelah membayar. Ia tak terlalu menyukai tempat itu, tapi juga tak melarang Joe dan anaknya pergi ke sana sesekali. Dalam hati kecil Set, mungkin ia akan merasa lebih baik jika sang ayah pernah marah padanya. Namun insinyur komputer tua itu terlalu merasa bersalah untuk menyalahkan orang lain selain dirinya. Hanya dengan berkonsentrasi bekerja, maka ia dapat sejenak menggantungkan rasa bersalah itu di sudut hatinya, untuk kemudian memakainya kembali seumur hidup. Bagi Set, Joe lebih mirip seperti ayah dan teman, sedangkan ayahnya sendiri hanyalah atasan bertingkah kaku yang bekerja sekantor dan tinggal serumah dengannya.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Saat bayangan malam mulai tampak, tiba-tiba bayangan yang telah lama bercokol dalam diri Joe bangkit. Tanpa ampun ia menyerang siapapun di dalam bar itu, termasuk Set. Tempat itupun berubah menjadi neraka pembantaian. Para petugas keamanan dan forensik segera datang membawa perisai besar, dan semburan gas air mata. Seluruh kota panik. Tempat teraman di kota itu ternyata tak lagi aman. &quot;Jangan tembak dia,.... aku bisa memperbaiki sirkuit chipnya, tolong jangan tembak,....&quot;, Set menjerit di tengah kekacauan. Di tengah dentuman peluru yang tak pernah menghiraukannya. Joe yang asli baru saja mati. Ia berubah menjadi mahluk zombie kejam. Menyerang dan membunuh siapapun yang ada di hadapannya. Termasuk Set, jika tak ada yang menyelamatkan pemuda itu. Saat Set membuka matanya, tampak sesosok tubuh yang tak asing, menerima setiap pukulan dan hujaman pecahan botol, serta sebersit peluru nyasar. Melindungi Set dari setiap macam bahaya yang ada. Ia tak lain adalah ayah Set sendiri. Sama seperti dulu ia berusaha melindungi Set dari kematian dan hukuman mati, hari ini iapun tetap akan melakukan itu, dalam kebisuan dan ketenangan yang tak tergoyahkan. Set hanya dapat diam mematung, menyaksikan tubuh sang ayah ambruk dalam posisi memeluknya. Sebuah pelukan yang bahkan tidak pernah ia rasakan sebelumnya.</font></p>\n<div style=\"page-break-after: always;\">\n	<span style=\"display: none;\">&nbsp;</span></div>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Hari itu, Set kehilangan kedua orang ayahnya. Seorang petugas kebersihan yang selalu membersihkan kebencian dari dalam hatinya, serta seorang insinyur komputer penuh kebisuan yang selalu berkorban untuknya. Pemuda itu menangis sebatang kara. Tak lagi peduli pada apapun. Hidupnya yang tanpa tujuan kini telah berubah. Ada satu hal yang sekarang harus dicapainya. Ia akan membalas dendam pada siapapun yang telah merenggut nyawa ayahnya dan nyawa Joe. Kebencian Set saat ini lebih besar dari apapun yang bisa digambarkannya. Sebuah kebencian yang pada akhirnya justru akan membawanya untuk menyelamatkan dunia.</font></p>\n', 0);
INSERT INTO blog_article (`article_id`, `article_title`, `article_url`, `date`, `author_user_id`, `content`, `allow_comment`) VALUES (4, 'Traceroute', 'Traceroute', '2012-12-13 01:07:50', 1, '<p>\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Jalanan dan lorong-lorong sempit itupun dilalui Set. Jalan-jalan di sebuah kota yang asing. Kota tempat ia dibesarkan dalam kesunyian. Ia merasa seperti seekor tikus yang telah kehilangan rumahnya. Di tangannya tergenggam sebuah chip. Chip yang berumur hampir sama dengan usianya. Chip itulah yang pernah ia modifikasi sewaktu masih berumur enam tahun. Kepingan logam yang pernah membuatnya hampir tewas. Kepingan logam yang menyimpan misteri besar. Jauh lebih besar dari ukurannya yang tak sampai seujung jari.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Waktu chip itu diambil, ayah Set diam-diam menyimpannya. Memberikan benda itu pada anak kesayangannya. Seorang pemuda kesepian yang dicintainya dalam diam. Saat ini tubuh sang ayah dan Joe terbaring lemas tak berbentuk di ruang otopsi. Menurut para petugas forensik, tak ada satupun yang tersisa dari chip di kepala Joe. Semua bekas malware seolah dihapus dengan paksa dan tergesa-gesa. Menyisakan kekosongan dan kebuasan pada pria baik yang kini telah tiada itu. Maka hanya ada satu hal yang tersisa dari tragedi itu. Sebuah kengerian, kebencian, dan sebuah tragedi lain yang tak cukup nyata untuk menjadi kenangan. Ayah Set dengan wajah kakunya pernah berkata, bahwa ia bisa membedakan baris program yang ditulis oleh Set, dan baris program lain yang bukan karyanya. Di dalam chip seujung jari itu tersimpan sebuah malware yang tak sempat diaktifkan. Seperti sebuah bukti kejahatan yang tak pernah terjadi. Kejahatan yang hampir merenggut nyawanya. Berkali-kali Set menggertakkan giginya. Mengapa nyawa itu tidak terenggut. Mengapa ia tidak mati, mengapa ia harus hidup untuk melihat begitu banyak kematian.</font></p>\n<div style=\"page-break-after: always;\">\n	<span style=\"display: none;\">&nbsp;</span></div>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Dalam setiap paket data yang terkirim, bisasanya akan ada dua informasi lain yang tak kalah penting. Ada asal, dan ada tujuan. Dua hal yang memastikan agar data itu tak salah arah dalam perjalanannya di lautan interconn. Informasi itulah yang dimanfaatkan Set dalam rasa penasarannya. Saat ini ia hanya ingin membalas dendam pada seseorang, entah siapa. Seseorang yang telah merenggut kehidupan dari dalam hidupnya. Set bahkan tak tahu siapa orang itu, dan sesungguhnya ia tak perlu tahu. Tapi seolah dalam pikirannya muncul sebuah keyakinan. Keyakinan akan apa yang akan dijalaninya. Set kembali melangkah maju, terbakar api kebencian yang seolah tak akan pernah padam ditelan waktu.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Suasana kota itu dipenuhi teror. Tidak ada yang keluar rumah. Beberapa orang memutuskan untuk tak berhubungan dengan interconn. Namun parahnya, hubungan dengan interconn, tak bisa dimatikan begitu saja. Orang-orang itu merasa seperti harus mencungkil kedua matanya sendiri, dan hidup dalam dunia purba tanpa dinamika informasi. Beberapa telah lupa bagaimana untuk memutuskan hubungan itu. Sangat ironis. Mereka bahkan mencari di interconn bagaimana cara lepas dari interconn. Mereka, siapapun itu, dapat tiba-tiba menjadi apapun seketika. Di tengah perjalanannya, kadang Set menemukan seseorang tengah mengamuk. Mungkin ia adalah suami, ayah, anak, atau kekasih seseorang. Ia kehilangan apapun selain dirinya, yang mungkin sebentar lagi juga akan ikut hilang. Persis sama seperti apa yang dialami Set.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Untuk terakhir kalinya, Set menoleh ke belakang. Memandang kota yang telah didiaminya sejak lahir. Sebuah kota dengan selapis kubah plasma kehijauan. Lapisan yang selalu melindungi kota itu dari radiasi. Kubah itu adalah karya ayah Eirene. Sebuah usaha untuk melindungi penduduknya atas segala bahaya dari luar. Namun kubah agung itu tak sanggup melakukan apapun untuk melindungi mereka dari maut. Cengkraman kuat yang merenggut setiap jiwa dalam ketakutan.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Set harus meninggalkan kota itu. Ia mungkin satu-satunya orang yang tak akan terpengaruh oleh serangan para hacker. Chip baru dikepalanya merupakan sebuah prototype yang sangat langka. Cukup langka, sampai tak seorangpun mampu menyabotasenya. Ketidak mampuannya untuk masuk ke interconn membuat tak seorang hacker pun dapat menyerangnya. Ia sendiri, berdiri di hadapan spesies yang mulai tumbang.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Ada sebuah gateway jaringan di utara. Sebuah tempat di mana paket-paket perenggut jiwa menyerbu kota mereka. Sebuah serbuan dalam diam, yang membaur bersama gelombang bit naik turun tanpa akhir. Mungkin dari sanalah segala sesuatunya berawal. Mungkin juga tidak. Hanya hacker bodoh yang akan langsung menyerang orang lain dari tempatnya berada. Paket-paket data itu biasanya berputar-putar dari dan ke segala arah, sebelum menyentuh otak-otak malang yang terhubung ke interconn.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Tapi Set tetap berjalan maju. Ia tak tahu apa lagi yang bisa dilakukannya. Kaki-kaki organiknya yang telah dimodifikasi dengan ribuan chip lain terus bergerak. Bahkan saat ia mulai tertidur. Jejak kakinya bergerak lemah pada hamparan padang pasir tanpa ujung, untuk selanjutnya hilang ditiup angin timur yang kering. Betapa kecilnya manusia di hadapan keluasan yang hening. Warna kebencian yang bergolak dalam diri Set seolah tak lebih dari titik biru pucat yang mengapung di tengah kegelapan alam semesta. Terkadang produksi asam laktat yang membanjiri otot-otot Set mengalahkan kebenciannya. Memaksa pemuda itu untuk beristirahat dengan mata setengah tertidur yang tak rela. Chip-chip non-organik itupun punya keterbatasan. Dua hari tanpa asupan listrik, kini telah membuat logam-logam kecil itu kehilangan tenaga. Set terpaksa menanggung semua itu sendirian. Dapat dirasakannya, sebagian kesadarannya telah hilang. Mirip seseorang yang kehilangan separuh otaknya.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Ada kalanya seseorang sangat ingin mati. Namun dalam keadaan seperti itupun, ia tak bisa mati. Bahkan syaraf-syaraf di tubuhnya terus menggeliat untuk secercah kehidupan. Set terus bergerak, di tengah gelap dan dinginnya malam yang mulai berganti menjadi terik tak tertahankan. Keinginan pemuda itu untuk tetap hidup telah menggeser sedikit rasa dendamnya. Ia berharap bisa menemukan seseorang atau sesuatu. Makanan, minuman, dan sumber listrik. Hanya itu yang paling dibutuhkannya sekarang.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Kadang Set hanya berputar-putar selama berjam-jam. Ia bahkan tidak sadar bahwa ia tersesat. Bagi seseorang dengan mata tertutup, sulit rasanya untuk tetap berjalan lurus. Demikian pula bagi seseorang yang hanya menemukan pasir, kemanapun ia memandang. Metode yang selama ini digunakannya untuk menentukan arah, mulai tak bekerja sebagaimana mestinya. Saat masih berpikiran waras, Set akan memandang matahari, melihat pada jam digital dalam chipnya, dan mengkalibrasi kesalahannya dalam berjalan, sambil menghitung posisinya yang baru. Tapi semenjak chip itu kekurangan tenaga, benda logam itu hanya seperti beban yang tak akan bisa dilepaskan seumur hidup. Benar-benar tak ada gunanya.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Mungkin sudah berhari-hari Set menghabiskan waktunya di sana. Mirip orang-orang yahudi yang keluar dari perbudakan Mesir. Tubuhnya tampak kurus dan lusuh. Kulitnya mulai terbakar dan terkelupas kecil. Sampai akhirnya ia menemukan suatu desa, kota, atau mungkin perkampungan. Bagi Set tak ada kata-kata yang tepat untuk menggambarkannya. Sekumpulan pria dan wanita hitam, dengan pakaian dari kain dan kulit seadanya, tinggal di rumah-rumah berukuran tiga kali tiga meter. Di perbatasannya tampak seorang tengah berjaga-jaga di atas menara yang terbuat dari kayu lapuk. Set tersenyum getir. Menara seperti itu pasti akan ambruk dengan sentuhan seekor kupu-kupu sekalipun.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Pemuda itu bangun dalam kegelapan. Kegelapan yang selama ini telah menyelimutinya. Kegelapan yang bahkan menyembunyikan terik matahari dan terus membuatnya berjalan tanpa arah. &quot;Kau sudah sadar?&quot;, seorang wanita muda yang kelihatan sangat tua membangunkannya. Hidup bertahun-tahun di bawah sengatan radiasi ultra-violet akan membuat siapapun menjadi tua lebih cepat. Siraman ultra-violet yang kejam itu akan mulai membakar kulit dan merontokkan segala macam rambut. Membuat sel-sel dalam tubuh seseorang berubah tak menentu, menjadikannya kanker ganas yang siap membunuh secara perlahan. &quot;Apa kau berasal dari koloni di sekitar sini?&quot;, tanya wanita itu. Di samping wanita itu, ada seorang anak muda. Tubuhnya hitam terbakar matahari. Sorot matanya begitu tajam menatap Set. Sebuah tatapan penuh rasa sakit dan kebencian. Tatapan seperti itulah yang akan dipandang pemuda itu saat ia keluar dari rumah sang wanita tak dikenal.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Namun mereka, setidaknya wanita itu, masih memperlakukan Set dengan cukup baik. Pemuda itu bisa merasakan ada arus listrik yang membasahi chip utama di kepalanya. Begitu berlimpah, sampai chip-chip lain di sekujur tubuhnya serasa turut hidup. Anak muda yang dari tadi menatap benci tanpa membuka mulutnya itu, rupanya adalah adik sang wanita. Wanita itu bernama Rin, sedangkan adiknya bernama Sin. Sepertinya nama asal-asalan yang tak berarti. Mungkin hanya memudahkan mereka untuk saling memanggil dan berbicara.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Dari Sin, Set tahu pedihnya penderitaan mereka. Hamparan padang gurun, air bawah tanah yang berbau busuk, serta dentuman-dentuman bom yang jatuh dari langit telah mewarnai hidup semua penduduk di sana. Tidak ada seorangpun yang bisa hidup sampai tua. Semuanya mati sebelum dewasa. Mereka seperti sejumput kehidupan yang dijejalkan di tengah padang gurun. Tidak ada gunanya sama sekali.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Set merasa sangat tertolak. Bukan dengan lemparan dan kata-kata kasar, tapi dengan sikap-sikap baik yang dipenuhi pandangan kebencian. Mungkin memang seperti itulah bentuk mata mereka. Proses mutasi bisa mengubah siapapun menjadi apapun. Set terus menghibur dirinya dengan penjelasan-penjelasan tak masuk akal.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Ia dan orang-orang itu berkomunikasi dengan bahasa yang dipaksakan. Ada banyak istilah, kata kerja, dan struktur kalimat yang sangat berbeda. Namun begitu, masih bisa dilihat bahwa semuanya memiliki akar bahasa yang sama. Maka merekapun, dengan susah payah, dan kadang sedikit kesalah-pahaman kecil, masih bisa berbicara satu sama lain. Untuk beberapa saat, Set berada di tempat terkutuk itu. Sebuah tempat yang sepertinya memang sengaja dikutuk oleh Tuhan dan manusia. Tempat cacing-cacing hitam dengan sorot mata aneh menolong seorang pemuda malang.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Di suatu senja yang tenang dan bisu. Saat timba-timba air tanah sudah selesai diangkut, saat seorang pemuda menggantikan temannya yang berdiri di menara, tiba tiba terdengar raungan di langit. Seberkas titik membelah angkasa. Semakin lama semakin besar. Lalu seluruh energi potensialnya berubah menjadi momentum mematikan di atas tanah. Bom-bom lainpun turut dijatuhkan. Orang-orang berhamburan, menuju bangker bawah tanah yang berpintu baja. Seorang ibu, tampak ketakutan memeluk anak-anaknya. Di depan pintu bangker yang belum terbuka, beberapa pria dewasa tewas terhujam pecahan bom. Kini Set adalah bagian dari komunitas itu. Chip-chip dalam tubuhnya mulai bereaksi. Memberikan tenaga pada otot-ototnya yang semakin melemah. Maka pintu bangker pun terbuka, dan lautan manusia menyeruak masuk ke dalam. Menuju pada kegelapan yang menyelamatkan. Set adalah orang terakhir yang masuk. Dilihatnya dari kejauhan seseorang berlari tanpa arah pada bangker-bangker yang telah hampir menutup, lalu kemudian terpental jauh, ditimpa bunga api dari angkasa.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Mungkin satu atau dua jam, serangan tanpa nama itu menghujani pemukiman mereka. Saat Set membuka pintu bangker dengan getaran rasa takut tak tertahankan, di depannya sudah ada sejumlah penduduk lain. Berdiri mematung menatap angkasa. Beberapa dari mereka mulai membersihkan pemukiman kumuh itu dari sisa-sisa kengerian, dan mayat-mayat tanpa nama. Di sana ada Sin, berlutut menatap sosok kakaknya. Rupanya Rin terlambat masuk ke dalam bangker, dan kini ia tengah sekarat. Set tidak tahu, apakah ia harus mendekat, atau tetap diam. Tapi kakinya melangkah sampai agak dekat. Menatap lekat-lekat wanita yang hendak meregang nyawanya. Tampaknya kehidupan masih setia menyiksa wanita itu, sebelum kematian menjemput.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Seketika itu nafas Set terasa sesak. Ia kira kematian Joe dan ayahnya, ketakutan orang-orang di kota, sudah merupakan penderitaan yang paling kejam. Namun kini, pemuda itu melihat, betapa ia telah meremehkan kata penderitaan. Waktu berjalan begitu lambat saat Sin mengambil sebilah pisau, dan menggorok leher Rin. Kedengarannya memang sangat sadis, tapi itulah satu-satunya kebaikan yang bisa membebaskan Rin dari kehidupan. Keduanya bukan satu-satunya korban. Ada belasan yang lain. Anak, suami, istri, dan kekasih seseorang yang meraung penuh benci dan ketidak berdayaan.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Sore itu juga, beberapa orang pria dewasa yang masih tersisa, membawa mayat-mayat itu ke sebuah bak terbuka. Membawanya dengan sebuah kendaraan usang, menuju ke tepian laut yang bermil-mil jauhnya dari pemukiman mereka. Satu-persatu, onggokan jasad organik itu ditelan habis ke dalam permukaan laut yang membisu. Riaknya membentur bebatuan dan pasir. Warna senja yang melankolis seolah tak tergoyahkan. Entah berapa banyak sudah mayat-mayat tak bernama yang ditelannya. Alam semesta seolah membisu dalam hening. Tak ada yang berubah.</font></p>\n', 0);
INSERT INTO blog_article (`article_id`, `article_title`, `article_url`, `date`, `author_user_id`, `content`, `allow_comment`) VALUES (5, 'Lagu Kehidupan', 'Lagu-Kehidupan', '2012-12-13 01:09:05', 1, '<p>\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Dulu, sebelum ada yang disebut agama, konon manusia hidup dalam ketakutan dan ketidak teraturan. Di Asia selatan, ada seseorang yang bernama Sidharta Gautama. Orang itu hidup dalam kebaikan dan kebijaksanaan yang sangat aneh di masanya. Lalu ia menjadi Buddha. Seorang yang tercerahkan. Sampai di masa kita, penganut agama ini begitu banyak. Terpisah oleh laut dan gunung, di suatu tempat yang jauh, muncullah sebuah agama monotheist. Ada sebuah bangsa yang disebut bangsa penjanjian. Umat yang dipilih oleh Allah sendiri. Umat yang di masa kita terus bermusuhan dengan negara-negara di sekitarnya. Ada agama yahudi yang mempersatukan mereka. Lalu dalam gelapnya waktu, muncul pula seorang yang mati disalib. Tidak semua orang mempercayai kematiannnya. Orang itu adalah Yesus, yang disembah orang-orang kristen sebagai Tuhan. Di tempat lain, muncul pula seorang utusan, yang menerima firman-firman dari Dzat Maha Suci. Seorang nabi penuh kebijaksanaan yang mengajarkan umatnya akan besarnya Tuhan. Di masa kita masih banyak orang-orang yang memegang teguh ajaran sang nabi. Ada banyak lagi orang-orang yang disebut suci. Mereka hidup dalam kebaikan yang tidak masuk akal. Mengajar pengikutnya untuk tetap setia pada kebenaran.</font></p>\n<div style=\"page-break-after: always;\">\n	<span style=\"display: none;\">&nbsp;</span></div>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Tapi apakah orang-orang suci itu tidak hanya sekedar menyampaikan omong kosong? Seorang lain bernama Epicurus. Orang yang sudah lama mati sebelum orang-orang suci itu dilahirkan, menggenggam erat sebuah pertanyaan. Jika ada Tuhan, jika Tuhan benar-benar maha baik, dan jika Ia benar-benar maha kuasa, mengapa Ia diam saja? Mengapa Ia diam dalam keheningan saat ada kejahatan? Keheningan yang seperti itu terasa sangat menakutkan, jauh lebih menakutkan dari kejahatan apapun. Keheningan yang menyebabkan para pengikut orang-orang suci itu bertindak sama memuakkannya dengan serigala-serigala licik. Keheningan yang melenyapkan segala macam kebenaran. Seolah-olah segala sesuatu adalah salah, dan semua orang harus hidup dalam kesalahan.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Di masa hidup Set, memang tidak banyak lagi agama yang masih tersisa. Tidak ada kuil, sinagoga, gereja, ataupun masjid. Bangunan-bangunan itu tidak lebih penting dari menara repeater medan plasma dan pembangkit listrik raksasa. Menara-menara teknologi menjulang dalam hati setiap penduduk federasi. Sebuah bentuk ketegaran manusia, menghadapi keheningan Tuhan yang abadi. </font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Namun di tengah kekacauan seperti itu, segala macam teknologi buatan manusia seolah tak ada artinya. Mereka mulai memandang dengan penuh kecurigaan dan kebencian. Saat itu, seperti halnya di jaman kita, lahirlah iblis-iblis paling mengerikan. Iblis itu bukan ular yang membujuk Hawa untuk memakan buah terlarang. Ia bukan pula malaikat yang jatuh dalam kenistaan. Iblis paling mengerikan itu adalah mahluk-mahluk rapuh berbahan dasar karbon yang disebut manusia. Iblis yang ada, hidup, menjadi besar, terluka dan melukai. Mereka tak bisa lepas dari lingkaran samsara yang dirajutnya sendiri. Berputar dari ketiadaan menuju kebinasaan.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Entah bagaimana, entah siapa, namun baik para manusia organik di dataran kumuh itu, maupun penghuni federasi yang angkuh menghadapi teror yang sama. Siapapun bisa mati kapan saja, dan alam semesta akan tetap membisu, berapa banyakpun nyawa-nyawa yang tercabut. Lama sekali Set berada di sana. Kebencian dan dendamnya tak kunjung padam. Rasa pahit bir yang ditenggaknya bersama Joe masih bisa dirasakannya. Demikian pula dengan pelukan dingin sang ayah. Setiap kali matanya terpejam, akan tampak bayangan itu. Sosok Sin yang menggorok leher Rin, dan belasan korban yang tergeletak tanpa nyawa.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Suatu siang, Sin membawa Set memasuki sebuah ruangan kecil dengan rak-rak yang terbuat dari kayu lapuk. Di tempat itu, mahluk-mahluk organik seperti Sin banyak belajar tentang keberadaannya di dunia. Sebuah keberadaan tanpa arti yang bagai sekam kering di tengah api. Tidak adil memang, jika mengingat betapa pendeknya umur mereka. Dan kadang kehidupan mereka masih juga direnggut oleh ulah sesamanya. Orang-orang tanpa nama yang melemparkan bom-bom kejam dari atas angkasa. Jelas itu ulah sekelompok orang dengan teknologi setingkat dengan federasi tempat kelahiran Set. Kadang para penduduk malang itu berpindah dari satu tempat ke tempat lain. Namun percuma, sepertinya langit memiliki mata untuk membidik mereka.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Di ruangan kecil itu terbaring sebuah buku lusuh. Sepanjang hidup Set, baru pertama kali ini ia menyentuh sebuah buku. Di tempat tinggalnya, semua data berbentuk digital. Terekam dalam alur magnetis, serat optik, atau apapun yang tak ada padanan katanya dalam bahasa kita. Yang jelas tidak ada selembar kertaspun. Saat melihat buku kusam yang tak terawat itu, Set bagai menemukan sesuatu yang ditulis ratusan generasi sebelum ia lahir. Dan memang begitulah kenyataannya.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Dengan terbata-bata, seolah membaca sebuah mantra kuno, Sin mulai membaca bagian akhir buku itu. Sebuah alasan yang menyebabkan kaumnya tetap bangga akan keorganikannya. Ada sebuah ramalan tentang tanda di bawah kulit yang akan dimiliki oleh setiap pengikut naga. Seekor binatang yang akan menghujat Tuhan di hari kiamat. Set dan Sin tenggelam dalam diam. Sebuah keheningan yang mengingatkan mereka pada heningnya laut senja yang menjemput mayat Rin. Keheningan itu adalah keheningan Sang Pencipta, yang bahkan tak menurunkan seorang malaikatpun untuk melindungi umat-umat Nya. </font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Maka mengertilah Set, mengapa tatapan kebencian itu selalu mengarah padanya. Bagi mereka, dia adalah sang pengikut iblis. Tapi Set sama sekali tak membenci mereka. Ia sama sekali tak ingin berdebat dan menambah perih luka dalam hati Sin. Set hanya bisa berterimakasih atas segala kebaikan yang diterimanya di tempat itu.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Sebelum Set pergi, ia masih berusaha mencari informasi. Menanyakan apakah mereka pernah tahu tentang serangan di interconn. Serangan para hacker yang merenggut setiap jiwa, menebarkan teror bagi mereka yang memiliki chip di kepalanya. Mungkin selama ini para hacker itu pula yang entah bagaimana menurunkan hujan bom kematian bagi mereka. Tapi tak ada satu orangpun yang tahu. Mereka tetap menatap Set dengan kebencian dan senyum yang dipaksakan.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Sama seperti ia memandang kota kelahirannya, demikian pula ia memandang pemukiman kumuh itu. Tempat orang-orang malang itu bertahan. Matahari senja mulai turun. Dalam keindahan tak terkatakan, sama seperti saat hujan bom membasahi angkasa. Diam membisu dalam hening.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">&quot;Aku akan membawamu ke koloni terdekat&quot;, kata Sin sebelum mereka menuju ke suatu tempat di utara. Sebuah koloni di bawah permukaan air. Tempat manusia-manusia organik dan para cyborg hidup terasing. Terlalu kecil untuk disebut federasi, namun masih cukup besar dibandingkan pemukiman tempat tinggal Sin. Langit mulai gelap, saat Sin meninggalkan Set di depan sebuah lubang. &quot;Dengar, orang asing, aku tidak membencimu. Tapi jangan pernah lagi kembali ke tempat kami,...&quot;, ucap pemuda itu dengan nada datar. Seolah tak ada apapun di sana, tak ada kebencian, tak ada ketakutan. Hanya ada kata. Seorang diri Set memasuki daerah baru itu. Ia bisa mencium bau peradaban. Memang jauh berbeda dengan kota asalnya, tapi masih layak disebut kota. &quot;Di sekitar sini&quot;. Set bisa merasakan keberadaan sesuatu yang telah lama dicarinya. Sebuah gateway yang menyebarkan malapetaka di dunianya.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Set pun mulai bergerak. Ditelusurinya lorong-lorong bawah tanah yang diterangi lampu halogen. Sesekali tampak seberkas sinar lain, sebuah kendaraan tanpa roda yang bergerak canggung ke arah berlawanan. Kendaraan itu mengangkut berbagai hasil tambang. Mungkin untuk diperdagangkan dengan koloni-koloni lain yang hidup di atas mereka. Tempat ini kelihatannya cukup baik. Jauh lebih baik dari pemukiman Sin yang kumuh. Penduduknya merupakan gabungan antara manusia-manusia organik murni dan para cyborg. Kebanyakan berada di antara keduanya. Ada yang memiliki mata buatan, ada pula yang sekedar memiliki port di belakang leher. Bentuk port mereka lebih besar dari yang dimiliki Set. Suku cadang organik, seperti bola mata, jantung, dan ginjal diperdagangkan dalam toples-toples kedap udara berisi cairan khusus. Berdampingan dengan segala peralatan cybernetic dan chip-chip terprogram.</font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font size=\"2\" style=\"font-size: 11pt\">Sayup-sayup dari kejauhan terdengar suara nyanyian. Seperti sebuah doa yang dipanjatkan. Kedengarannya sedih, seperti anak kecil yang kesepian.</font></p>\n<p style=\"margin-left: 0.56in\">\n	<br />\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\"><span lang=\"zxx\"><i>bokura wa minna ikite iru</i></span></font></font></font><br />\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\"><span lang=\"zxx\"><i>ikite iru kara utau n&#39; da</i></span></font></font></font><br />\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\"><span lang=\"zxx\"><i>bokura wa minna ikite iru</i></span></font></font></font><br />\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\"><span lang=\"zxx\"><i>ikite iru kara kanashii n&#39; da</i></span></font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Set sudah terlalu lelah. Ia tak tertarik untuk tahu apa arti liriknya. Ia juga tak tertarik untuk tahu dari mana sumber suara itu berasal. Sementara ia memandang pada kehampaan di tengah ramainya kota itu, ada seseorang yang lain. Seorang anak kecil berbaju kotor. Ikut mengulangi lirik-lirik misterius itu. &quot;Kita semua hidup. Karena itu kita bernyanyi. Kita semua hidup. Karena itu kita sedih&quot;. Seolah ada getaran kegembiraan dan kesedihan yang membuatnya terus menyanyi.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Di sini, di bawah lapisan air yang telah menenggelamkan daratan. Ada sebuah dunia lain. Dunia yang tenggelam dalam kebisuan. Dunia yang begitu tenang. Set merasa sangat lelah. Apa yang sudah terjadi di atas sana telah banyak menguras emosinya. Mungkin kota asing ini bisa menjadi rumah barunya. Sebuah rumah untuk melepaskan segenap dendam dan kebenciannya. Dengan mata setengah terpejam, Set bersandar pada sebuah tiang beton. Kelak Set tak akan pernah melupakan tempat ini. Sebuah tempat di mana ia bisa melepas sementara segenap beban hidup yang menyesakkan.</font></font></font></p>\n', 0);
INSERT INTO blog_article (`article_id`, `article_title`, `article_url`, `date`, `author_user_id`, `content`, `allow_comment`) VALUES (7, 'Gadis Tanpa Senyuman', 'Gadis-Tanpa-Senyuman', '2012-12-13 01:10:23', 1, '<p>\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Tidak ada yang lebih nyata dari sebuah ilusi, dan tidak ada yang lebih khayal dari sebuah kenyataan. Mungkin Set sedang bermimpi. Mungkin tahun-tahun yang telah dilaluinya, kecelakaan yang menimpa Eirene, kematian Joe dan ayahnya, hujan bom dari angkasa, semua itu hanyalah ilusi. Dan saat ini ia terbangun di sebuah tempat asing. Menghirup kenyataan untuk pertama kalinya. Atau justru sebaliknya, tempat ini, lirik-lirik lagu yang misterius dan emosional, semuanya hanyalah mimpi.</font></font></font></p>\n<div style=\"page-break-after: always;\">\n	<span style=\"display: none;\">&nbsp;</span></div>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">&quot;Ayo minggir, ini tempat kami,....&quot;, sekumpulan mahluk cybernetic membangunkan Set dari tidurnya. Dengan malas, Set menyingkir dari tempatnya. Sesekali ia menoleh, menatap sebuah mata buatan yang dipasang asal-asalan. Tampak mahluk-mahluk itu tak terlalu menyukai keberadaan Set. Maka pemuda itu mengurungkan niatnya untuk berbaik hati. Sebagaimana seorang dokter di jaman kita, Set cukup tergerak untuk menolong mahluk-mahluk cybernetic itu. Seorang insinyur komputer sepertinya akan sangat paham mengenai operasi cybernetic yang benar. Kelihatan jelas bagian-bagian tubuh mereka cuma asal ditempelkan, menambal lubang ketiadaan yang semula didiami jaringan-jaringan organik.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Sayup-sayup, Set kembali mendengar lirik lagu misterius itu. &quot;Kita semua hidup, karena itu kita menyanyi. Kita semua hidup, karena itu kita sedih&quot;. Suara itu, entah benar-benar ada, atau cuma kesalahan pada sirkut di kepalanya terus menemani perjalan Set. Menempuh lorong-lorong asing di sebuah kota asing. Lorong-lorong hatinya sendiri yang dipenuhi kegelapan. Setiap kali memandang ke atas, Set tak bisa menemukan langit. Hanya sinar lampu halogen redup, mencoba menerangi setiap langkahnya. &quot;Lama tidak ketemu, Set Asgard....&quot;, ada sebuah suara asing. Tapi Set merasa pernah mendengar warna suara yang sama, entah di mana. Di belakangnya berdiri seorang wanita cantik dengan jubah panjang. Eirene Stark kini telah tumbuh dewasa. Set bisa mengingat bekas luka operasi di pelipis Eirene. Saat itu tak ada sepatah katapun yang bisa keluar dari mulut Set. Kalau bisa, ia ingin lari saja. Menghindar jauh-jauh dari masa lalu yang selalu mempersalahkannya.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Untuk beberapa saat, keduanya saling memandang. Mencari sekumpulan kata untuk dipertukarkan. Tapi semuanya kembali tertelan sebelum sempat terucap. Menyisakan keheningan mencekam di antara kedua sahabat lama itu. Sesekali tatapan Eirene melukai relung hati Set. Tatapan itu tak lagi tampak seperti tatapan seorang sahabat. Keduanya masih saling mencari. Berusaha menemukan sosok sahabat masa kecil mereka. Namun sia-sia, Set dan Eirene seolah tak sanggup saling mengenali. &quot;Lama tidak ketemu,...&quot;, sapa Set memecah keheningan. Ada senyum getir yang tersungging di wajahnya. Eirene mengangguk tanpa ekspresi. Percakapan mereka terdengar sangat kaku. Rasanya lebih baik diam daripada bercakap-cakap seperti itu. &quot;Maafkan aku,...&quot;, Permintaan maaf yang telah terpendam bertahun-tahun itupun akhirnya terucap. Eirene kembali mengangguk, tanpa ekspresi ataupun senyum. &quot;Bagaimana kau bisa ada di sini?...&quot;, suara mereka terdengar saling bertabrakan. Lalu suasana kembali hening.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Sekedar basa-basi, Set mencoba menanyakan tentang keluarga Eirene. Tentang kota kelahiran mereka yang terletak jauh di atas sana. Namun jawaban gadis itu sangatlah mengejutkan. Eirene menggelengkan kepalanya, &quot;Kota kita sudah tidak ada&quot;. Dua hari setelah kepergian Set, serangan mematikan menyerbu kota mereka. Setiap sirkuit dalam jaringan otak penduduk telah dikuasai para hacker. Lalu ledakan bom pun membanjiri angkasa, menghanguskan setiap puing yang tersisa. Eirene sangat beruntung bisa selamat dari tragedi itu. Sama beruntungnya dengan Set. Kini, mungkin mereka berdualah satu-satunya yang tersisa. Seperti fosil hidup dari sebuah peradaban.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Sepanjang perjalanan tanpa tujuan, Eirene terus memperhatikan Set dalam diam, mencari apapun yang tersisa dari seorang anak delapan tahun bernama &#39;Set Asgard&#39;. Sama seperti sebelumnya, gadis itu tak lagi bisa menemukan apapun. &quot;Eirene, apa kau benar-benar sudah memaafkanku....&quot;, tiba-tiba rasa bersalah itu kembali membanjiri hati Set. Lagipula rasanya sangat aneh, saat Eirene menanggapi permintaan maafnya dengan sebuah anggukan kecil. Dalam lubuk hatinya, Set berharap akan mendengar sebuah amarah yang terpendam. Baginya itu jauh lebih baik daripada sekedar pengampunan yang penuh kebohongan. &quot;Tidak Set, kau sudah sangat melukaiku. Kebodohanmu sudah membakar sebagian jaringan syarafku. Dan sejak itu, aku tak pernah lagi bisa tersenyum atau menangis&quot;. Kata-kata Eirene terdengar datar, tanpa ada kebencian di dalamnya. &quot;Tapi bukan itu yang membuatku tak bisa memaafkanmu. Kau pergi dan tak pernah datang lagi. Padahal aku sangat merindukanmu. Sangat merindukan satu-satunya teman yang kumiliki&quot;. Eirene tetap berjalan, tanpa ekspresi. Ia tak sanggup menangis, seberapapun ia ingin menangis. </font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Lama setelah itu, tak ada lagi kata-kata yang keluar dari mulut mereka. Set terlalu merasa bersalah, sampai-sampai ia sudah melakukan sebuah kesalahan lain yang jauh lebih besar. Dalam diam keduanya memasuki sebuah gerbong kereta api bawah tanah, menuju ke pusat kota. Sekali lagi, dalam kepala Set, masih terdengar sayup-sayup dari kejauhan. &quot;Kita semua hidup, karena itu kita bernyanyi. Kita semua hidup, karena itu kita sedih&quot;.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Ada sebuah distrik di pusat kota bawah air itu. Di sana selalu terdengar dentuman-dentuman monoton. Seperti suara puluhan pekerja yang tenggelam dalam kesibukannya. Beberapa mesin besar mengubah logam-logam mentah menjadi suku cadang mekanik. Set mengamati sekelilingnya, memandang kagum pada mesin-mesin yang bekerja tanpa henti. Seolah tak peduli akan apapun yang terjadi di luar sana. Lalu ia mulai teringat akan laut itu. Laut senja yang menelan kematian Rin dan belasan orang lainnya.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">&quot;Sekarang ini rumahku&quot;, kata Eirene, &quot;dan kau juga bisa tinggal di sini&quot;. Set dan Eirene merasa seperti dua orang asing. Hanya ikatan masa lalu saja yang membuat mereka tetap bisa saling bicara. &quot;Kau sudah jauh berbeda, Set. Sepertinya ada banyak hal yang harus kita bicarakan&quot;, Eirene mempersilahkan Set untuk masuk ke sebuah ruangan kecil. Tidak sebesar rumah-rumah di kota kelahiran mereka, namun masih cukup pantas untuk disebut tempat tinggal. Setiap kali Set ingin berbicara, seolah ada kata maaf yang tertahan. Rasanya begitu menyesakkan. Sungguh ia ingin lari dari tempat itu.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">&quot;Aku sudah mencarimu. Mencari di setiap tempat yang mungkin. Kudengar ayahmu meninggal dalam serangan mengerikan di bar&quot;, Eirene memalingkan wajahnya ke arah Set. Seperti wajah boneka cantik yang tanpa emosi. Set selalu merasa terpukul melihat wajah teman masa kecilnya itu. &quot;Namun, justru kau ada di sini. Di depanku dengan wajah yang terus menunduk&quot;. Pembicaraan itupun semakin kaku. Set menceritakan segala sesuatu yang sudah terjadi selama ini. Bagaimana ia merasa begitu takut, bahkan hanya untuk sekedar meminta maaf pada gadis itu. Bagaimana rasa bersalah itu terus menghukumnya. Ia bercerita tentang Joe, tentang ayahnya, tentang kebenciannya, tentang pemukiman kumuh di tengah gurun, dan tentang kematian seorang manusia organik bernama Rin. Lalu Set mulai menceritakan tentang laut yang sunyi. Tentang tempat ini. Tempat ia bisa menemukan sejumput kedamaian dalam hidupnya. Tentang Eirene.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Eirene berusaha keras untuk bisa tersenyum. Sebuah usaha yang pasti gagal. Dipandangnya lekat-lekat sosok pria kurus yang sudah terlalu lelah mengarungi tragedi itu. &quot;Kau tidak apa-apa?&quot;, tanyanya. Eirene menghela nafas dalam-dalam. Memenuhi paru-paru organiknya dengan udara pengap yang dipompakan ke dalam kota itu. Eirene belum bisa sepenuhnya memaafkan Set. Tapi ia sudah bisa menerimanya. Jauh sebelum Set menceritakan semua itu. Tragedi-tragedi yang di telinganya cuma terdengar bagai usaha membela diri. &quot;Sungguh ironis. Padahal kau pernah janji untuk membawa kita ke interconn. Kini tak satupun dari kita bisa melihat dunia itu. Dunia tempat semua jiwa menari di antara aliran informasi. Dunia yang memisahkan kita selama sekian tahun&quot;. Keduanya kembali membisu. Berusaha saling memahami keberadaan satu sama lain. Mereka mungkin masih bisa berteman, tapi dalam ikatan pertemanan yang berbeda. Ikatan yang saling menyimpan luka.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Di luar sana ada seorang pemuda yang lain. Menginspeksi mesin-mesin raksasa yang terus menerus berbisik dengan dentuman menyakitkan. Wajahnya sangat rupawan. Terlalu indah hingga wanita manapun akan jatuh cinta padanya. Terlalu tampan hingga laki-laki manapun akan mati iri dibuatnya. Selama pemuda itu melakukan pekerjaannya, ia tetap diam. Terkadang seolah muncul aura misterius dalam ketenangannya. Orang-orang disekitar sang pemuda, memanggil mereka Hiro. Sebuah nama yang memiliki arti luas.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Hiro Matsu terlahir di kota tanpa nama itu. Tinggal bersama ibunya. Sama seperti Set, ia adalah seorang insinyur komputer. Orang-orang yang lebih banyak diam dalam kehidupannya. Setiap kali memandang Hiro, Set merasa seperti melihat versi lain dari dirinya. Sebuah keberadaan tanpa kesalahan yang jauh lebih baik darinya. Set dapat melihat bahwa Eirene juga memandang pria itu dengan pandangan yang hampir sama. Entah mengapa, itu terasa sangat menyakitkan.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Terkadang Hiro melakukan perjalanan ke federasi-federasi lain. Membicarakan model chip terbaru, protokol keamanan yang lebih baik, serta hal-hal lain yang bagi sebagian orang terdengar sulit. Seandainya Set tidak pergi dari kota kelahirannya, mungkin ia sudah bertemu dengan Hiro. Rasanya pertemuan mereka memang sudah ditakdirkan, dan keluarnya Set dari federasi hanya menunda pertemuan mereka beberapa saat. Konon Hiro juga pernah berkunjung ke federasi itu, tempat tinggal Set dan Eirene. Dan Hiro lah yang menyelamatkan Eirene dari serangan bom yang menyeruak bagai ledakan bunga cherry. Maka cukup masuk akal, jika Hiro cukup dekat dengan Eirene.<br />\n	<br />\n	Ketika Set dan Hiro membicarakan hal-hal teknis, keduanya menjadi sangat bersemangat. Seolah tak ada hal lain yang lebih penting. Mereka bisa berbicara selama beberapa jam tanpa berhenti. Di saat itulah Eirene merasa dirinya sama sekali tak penting bagi kedua pria itu. Sebuah perasaan yang entah mengapa terasa sangat menyakitkan.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Tapi kedekatan Set dan Hiro hanya sampai di situ. Mereka sama-sama insinyur komputer. Mereka menghabiskan seumur hidup mereka untuk hal-hal yang sama. Secara pribadi Set tidak menyukai Hiro. Setiap kali pemuda berwajah asia itu membicarakan hal-hal non teknis, Set serasa ingin merobek bibirnya. Entah apakah Hiro merasakan hal yang sama. Terkadang ia pun harus menahan kantuk, mendengar bicara Set yang lambat. Entah bagaimana, terkadang Set senang sekali membayangkan Hiro sebagai sosok hacker di balik semua itu. Sosok malaikat tampan yang telah berubah menjadi iblis.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Sinar lampu halogen kebiruan kembali memudar. Alur siang dan malam di kota itu diatur oleh mesin-mesin digital-mekanis. Maka jiwa-jiwa penduduknya pun kembali tertidur dalam damai. Begitu tentram. Mungkin dalam kegelapan itu hanya ada sepasang mata yang masih terbuka. Eirene menatap sedih dalam kegelapan. Tanpa ekspresi. Wajahnya seperti wajah boneka cantik. Setidaknya saat ini ada Set di samping Eirene. Sosok yang selalu dirindukannya dalam gelap, dalam cinta yang tak pernah bisa dimengerti.</font></font></font></p>\n', 0);
INSERT INTO blog_article (`article_id`, `article_title`, `article_url`, `date`, `author_user_id`, `content`, `allow_comment`) VALUES (8, 'Seorang Teman', 'Seorang-Teman', '2012-12-13 01:11:20', 1, '<p>\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Dentuman-dentuman mesin itu masih juga tak kunjung berhenti. Persis sama seperti detakan jantung yang menjaga sebuah kehidupan. Dari dalamnya terpompa aliran ekonomi yang memberi nafas pada sebuah kota. Jauh di sini, di bawah permukaan laut yang tenang, ada orang-orang yang masih bisa bertahan.</font></font></font></p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Pada masa-masa paling sunyi dalam kehidupannya, Set kembali teringat akan rumah. Sebuah ruang hati yang begitu gelap. Hanya ada ayahnya, Joe, dan Eirene. Kalau bisa ia ingin berakhir seperti ini. Di samping seorang gadis yang pernah menjadi bagian penting dalam hidupnya. Namun selalu ada hal lain di dalam kegelapan itu. Ada sebuah kebencian yang terus meraung. Ia teringat akan Sin, Rin, dan segenap penghuni padang pasir. Ia teringat akan orang-orang di kota kelahirannya. Ia bisa membayangkan bagaimana orang-orang itu berlari ketakutan, mengejar secercah harapan yang tak pernah ada. Sementara itu, sang hacker misterius, seseorang yang begitu jauh, seseorang yang begitu kuat, seolah begitu puas dengan semuanya. Persis sama seperti seorang anak kecil yang membanjiri sarang semut dengan air keran. Tidak ada kebencian atau alasan apapun di balik kekejamannya. Hanya ada sebuah kesenangan.<br />\n	<br />\n	Set sama sekali tidak tahu siapa di balik semua tragedi itu, namun akan sangat mudah untuk membayangkan Hiro yang duduk tersenyum di suatu tempat. Mungkin dialah yang menurunkan hujan ledakan dari atas sana, dan merenggut setiap nyawa dalam kehampaan. Hiro Matsu, seorang insinyur komputer misterius. Terlalu misterius untuk tidak dicurigai. Setiap kali berbicara dengannya, Set selalu merasa tersakiti. Seharusnya Set yang berada di sana untuk menolong Eirene, bukan Hiro. Tapi seolah Hiro tak tahu tentang perasaan Set. Atau tepatnya tak peduli. Di mata Hiro, Set justru adalah seorang teman. Seseorang yang seolah sudah lama ia kenal. Keduanya bisa saling menemukan sosok masing-masing saat berbicara satu sama lain. Hiro Matsu dan Set Asgard, keduanya seolah terperangkap dalam suatu persahabatan yang aneh.</font></font></font></p>\n<div style=\"page-break-after: always;\">\n	<span style=\"display: none;\">&nbsp;</span></div>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">&quot;Aku akan membantu Hiro. Ada sebuah proyek besar yang tidak bisa dia selesaikan sendiri&quot;, ucap Set pada Eirene. Setelah beberapa saat, gadis itupun menjawab dengan datar, &quot;Iya, aku juga akan mencari pekerjaan. Kita tidak bisa hidup begini terus&quot;. Dalam hati, keduanya tertawa geli. Kata-kata mereka terdengar seperti percakapan suami istri. Maka Set pun terdiam dengan wajah memerah. Rasanya benar-benar aneh. Padahal sewaktu masih kecil ia dan Eirene bisa membicarakan apapun. Mulai dari hal-hal serius sampai omong kosong tidak jelas. Namun kini mereka sering terdiam dalam keheningan. Saling menatap tanpa mengatakan apapun. Mungkin di tempat ini, Set dan Eirene bisa memulai hidup mereka dari awal. Menatap masa depan, sambil menggenggam kenangan masa lalu. Kenangan pahit dan manis yang hanya milik mereka berdua.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">&quot;Kau sudah mengatakannya pada Eirene?&quot;, tanya Hiro memastikan. &quot;Kalian memang tidak bisa hidup begitu terus, kau kan laki-laki, Set&quot;, Hiro yang biasanya pendiam itu terus mengoceh. Dan hanya Set yang bisa mendengar ocehan itu. Kepada orang lain, Hiro selalu tampak sebagai sosok yang sangat dingin. &quot;Aku dan dia cuma teman&quot;, wajah Set kembali memerah. Keduanya menghela nafas panjang. Saat sedang bekerja, Hiro tidak pernah mengatakan kata-kata apapun yang tidak penting. Persis sama dengan Set. Mereka akan tenggelam dalam pembuatan chip-chip baru. Mungkin ini bisa melindungi generasi-generasi sesudah mereka dari serangan hacker. Tapi tentu saja semuanya akan sia-sia jika ternyata kecurigaan Set benar. Sampai saat inipun ia masih memandang Hiro dengan penuh curiga.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Beberapa mil dari tempat mereka, Eirene tampak sibuk di hadapan sebuah komputer tua. Ia dan Set adalah salah satu bentuk ironisme. Chip-chip mereka bisa langsung terhubung ke interconn, seandainya mereka tahu nomor seri dalam chip mereka. Tapi orang tua mereka telah mengubur jauh-jauh deretan nomor pembawa bencana itu. Kini Eirene hanya dapat menatap layar touch screen, memandang dunia interconn dari balik jendela kaca. Dalam hatinya, gadis itu juga mengalami kepedihan yang sama dengan Set. Kepedihan karena terus hidup dalam keheningan. Eirene memiliki kejeniusan yang berbeda dari Set dan Hiro. Ia bukan seorang insinyur komputer. Tapi ia sangat ahli dalam melihat hal-hal tersembunyi. Hal-hal yang tak bisa dilihat orang lain. Sewaktu masih di akademi Shinra, teman-teman Eirene menjulukinya dengan sebutan poker face. Tidak ada yang bisa menebak pikiran gadis itu. Namun dibalik wajah cantiknya yang tanpa ekspresi, Eirene selalu tahu apa yang dipikirkan teman-temannya. Kedengarannya memang cukup mengerikan. </font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Setelah sekian lama mencari, sampailah gadis itu pada sebuah kesimpulan. Kesimpulan yang sama dengan dugaan Set. Ada sebuah gateway di kota ini. Tempat malware-malware perebut nyawa itu berputar keluar masuk, merasuki jiwa-jiwa yang tak pernah siap. Dan tampaknya ada sesuatu di kota ini. Sesuatu yang cukup kuat untuk mengendalikan semuanya.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Sinar-sinar lampu halogen mulai meredup. Seolah hendak mengatakan bahwa hari sudah sore. Hiro mengajak Set ke sebuah bar di dekat tempat mereka bekerja. Sebuah ajakan yang mengingatkan pemuda itu akan kebiasaan Joe. &quot;Interconn sudah berbeda&quot;, Hiro mengawali pembicaraannya. &quot;Kau tak ingin ke sana? Aku bisa mengusahakan untuk mencari nomor seri chip di kepalamu, walaupun aku tidak bisa mengetahui password nya&quot;, kali ini Hiro serius. Set tercengang. Sekilas ia merasakan ancaman yang begitu besar. Walaupun Hiro tidak mengetahui passwordnya, bukankah bagi insinyur komputer seperti mereka, akan sangat mudah untuk mencari tahu. Kombinasi angka dan huruf secara acak akan bisa menjebol password setangguh apapun. &quot;Aku serius, Set, aku tidak bisa mengetahui password mu,...&quot;, Hiro kembali menegaskan kata-katamu. Set tertawa sinis. Disebutkannya password chip milik Hiro, seolah hendak mengingatkan pria itu bahwa mereka berdua adalah insinyur komputer. Tentu saja Hiro langsung melompat dan memberangus mulut Set. &quot;Kau gila, kenapa kau menyebutkan password ku di tempat ini&quot;. </font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Hiro memang pernah mencoba mencari password chip dalam kepala Set. Sama halnya seperti para script kiddies amatir di masa kita, para insinyur di masa itu pun kerap mencari nomor seri chip dan passsword seseorang. Bukan untuk berbuat jahat, hanya untuk kesenangan sambil mengasah kemampuan. Tapi saat menjadikan Set sebagai target, usaha Hiro sama sekali tidak pernah berhasil. Ia merasa seperti melihat sesosok pria tua yang dingin, seorang yang selalu melindungi Set dari bahaya apapun, termasuk kematian.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Chip dalam kepala Set memang dirancang dengan sangat khusus oleh sang ayah. Ia tak mau anaknya melakukan modifikasi sebelum ia siap. Dan bagi seorang ayah, sehebat apapun anaknya, ia seolah tidak akan pernah siap. Malam itu Hiro merasa sangat kesal dengan kekonyolan Set. Niat baiknya dibalas dengan cara yang aneh. Susah payah ia merombak dan menulis ulang protokol keamanannya. Kini password lamanya tak akan pernah kembali berfungsi. Hiro tersenyum kecut. Menertawakan usaha sia-sianya. Jika ia tahu bagaimana cara membuat protokol itu, maka suatu saat Set akan menemukan cara yang sama untuk membobolnya. Dan entah mengapa, Hiro merasa Set benar-benar akan melakukannya.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">&quot;Set, aku tidak bohong,.... apa kau sendiri tidak tahu bagaimana rumitnya protokol keamanan dalam chip di kepalamu?&quot;. Keesokan harinya, di bar yang sama, Hiro mengungkapkan semuanya. Set terdiam, memejamkan mata, dan kemudian tersentak kaget. Benar yang dikatakan Hiro. Protokol keamanan itu benar-benar rumit. Seperti bukan ditulis oleh manusia. &quot;Ini pasti pekerjaan ayah,... dasar laki-laki tua brengsek&quot;, gumam Set dalam hati. Pemuda itu begitu membenci sekaligus menyayangi ayahnya. Lalu sejenak ia teringat pada tubuh dingin bersimbah darah yang memeluknya tanpa nyawa. Rupanya sang ayah masih tetap melindungi Set sampai sekarang. &quot;Protokol keamananmu juga tidak kalah rumit, Hiro&quot;, balas Set santai. Entah sejak kapan mereka jadi semakin akrab. &quot;Sialan kau, Set. Kalau kau mencoba menjebolnya lagi, akan kupatahkan lehermu&quot;, Hiro menenggak minumannya dengan kesal. &quot;Patahkan saja leherku, dan proyek itu tidak akan pernah selesai&quot;, balas Set ketus.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Beberapa saat setelah itu, Hiro tak lagi mengajak Set berkunjung ke bar. Ia berkonsentrasi pada pekerjaan sampingannya. Sebuah janji pada seorang teman. Dari jalur distribusi chip, pemuda itu berhasil menemukan sejumlah nomor seri yang mungkin salah satunya cocok dengan Set. Hiro merasa harus melakukan semuanya. Rasanya seperti ada sebuah perintah. Set sempat terkejut dengan usaha keras Hiro. &quot;Hanya untuk kesenangan&quot;, ucap laki-laki itu saat menyerahkan lima ratus record berisi daftar nomor seri. Seolah kata-kata Hiro menjelaskan semuanya. Bagi orang-orang seperti Hiro, tidak ada motivasi yang lebih tinggi dari sebuah kesenangan. </font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">&quot;Ada gateway network di sekitar sini, apa itu yang membawamu ke sini?&quot;, tanya Eirene di suatu malam. &quot;Rahasia apa saja yang ada dalam kepala seorang insinyur komputer?&quot;, tanyanya lagi tanpa mengharapkan jawaban. Set menghela panjang nafasnya. Ia tak bisa menebak maksud di balik kata-kata Eirene. Mengapa wanita selalu berbicara dengan bahasa berputar-putar, mirip paket-paket data yang melewati banyak router. &quot;Apa kau curiga pada Hiro?&quot;, tiba-tiba Eirene meluncurkan kata-kata yang selama ini ada dalam benaknya. &quot;Apakah ia ada di balik semua ini?&quot;, gadis itu menatap lekat-lekat ke arah Set. &quot;Tempat ini begitu damai dan tenang, terlalu damai di tengah kondisi dunia yang tengah meringkuk ketakutan&quot;, kali ini Eirene harus sedikit berteriak. Ia mulai kesal dengan tanggapan Set yang selalu diam. Rasanya jarak di antara hati mereka sudah sedemikian jauh.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Set tercengang. Ia tak tahu bahwa Eirene bisa memiliki pikiran sejauh itu. Gadis itu sudah bukan lagi teman masa kecilnya. Sesaat kemudian, keduanya pun kembali diam, seolah ada tembok waktu yang memisahkan kedua sahabat masa kecil itu. Maka Eirene pun tertidur setelah mengucapkan selamat malam yang penuh kekakuan. Ini konyol sekali, gumam Set dalam hati. Diraihnya memori eksternal itu, memori eksternal yang diberikan oleh Hiro. Mungkin beberapa bulan lagi ia akan bisa kembali melihat interconn. Dunia masa kecilnya. Dunia yang saat ini tengah meraung ketakutan.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Pertanyaan-pertanyaan Eirene, terasa begitu mengganggu benak Set. &quot;Hanya untuk kesenangan&quot;, kini kata-kata Hiro sama sekali tidak terdengar masuk akal. Bagi Set, Hiro bersikap terlalu baik untuk disebut teman, dan terlalu mengerikan untuk disebut musuh.</font></font></font></p>\n<p lang=\"zxx\">\n	<br />\n	&nbsp;</p>\n<p lang=\"zxx\">\n	<font color=\"#333333\"><font face=\"Times New Roman, serif\"><font size=\"2\" style=\"font-size: 11pt\">Set pun tertidur di samping Eirene. Esok pagi, mungkin keduanya akan terbangun dengan sebuah kehidupan baru, melayang dalam ketidakpastian alam semesta.</font></font></font></p>\n', 0);
